import scipy.stats
compare =  [matrix_r, matrix]
col_name = ["Measure",  "Mean", "Distribution"]
Level = ["Exponential",  "Lognormal"]
m = pd.DataFrame (columns = col_name)
new_entry = { c : 0 for c in col_name}
b = 0

for c in compare:
    for key in matrix.columns:
        if key == "Porportion_Abandonned":
            new_entry.update( Measure = "Porportion Abandoned")
        elif key == "Porportion_Waiting":
            new_entry.update( Measure = "Porportion Waiting")
        elif key == "Queue_Fraction":
            new_entry.update( Measure = "Fraction Queuing (Time Weighted)")
        elif key == "Waiting_Time_A":
            new_entry.update( Measure = "Waiting time - A")
        elif key == "Waiting_Time_S":
            new_entry.update( Measure = "Waiting time - S")
        elif key == "Utilization":
            new_entry.update( Measure = "Server Utilization")
        elif key == "Avg_People":
            new_entry.update( Measure = "People (Time Weighted)")
        elif key == "Avg_Queue":
            new_entry.update( Measure = "Queue (Time Weighted)")
        elif key == "Waiting":
            new_entry.update( Measure = "Waited")
        else:
            new_entry.update( Measure = key)
        new_entry.update( Mean =  round(c[key].mean(), 3))
        new_entry.update( Distribution = Level[b] )
        new_entry.update( CoeffOfVar = 100*(np.sqrt(statistics.variance(c[key])))/c[key].mean())
        intervl = scipy.stats.t.ppf(q=.975,df=19)*np.sqrt(statistics.variance(c[key]))/np.sqrt(20)
        new_entry.update( ConfidenceInterval = "("+str(round(c[key].mean() - intervl, 5))+ ","+ str(round(c[key].mean() + intervl, 5))+")")
        m = m.append(new_entry, ignore_index = True)
    b+=1
